import React, { useState , useEffect  }from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import icon from '../img/icone.png';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; 
import './css/HeaderNavbar.css';
import { UserContext } from './components/UserContext';


const HeaderNavbar = () => {


  const navigate = useNavigate();
  //const [username, setUsername] = useState('');
  //const [roleAdmin, setRoleAdmin] = useState('');

  const name = "";
  const role = false;
  
  const { user, tokenExpired} = useContext(UserContext); 

  useEffect(() => {
    //const token = localStorage.getItem('accessToken');
    if (tokenExpired  || !user) {

      navigate('/login'); // Redireciona para a página de login se não houver token
      

    } else {

      //const decodedToken = jwtDecode(token);
      //const 
      name = user.name ; //decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'];
      //setUsername(name); 
      
      //const role = user.role ; //decodedToken['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'];
      //setRoleAdmin(role === 'Admin' ? true : false);
      role = (user.role === 'Admin' ? true : false);

    }
  }, [navigate,tokenExpired , user]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    navigate('/login');
  };


  const handleUsuarios = () => {
    navigate('/usuarios');
  };

  const handleChamados = () => {
    navigate('/chamados');
  };

  const handleTrocarSenha = () => {
    navigate('/trocarSenha');
  };
  

  return (
    <Navbar bg="light" expand="lg" className="custom-navbar">
      <Container>
        <Navbar.Brand href="#home" className="d-flex align-items-center" onClick={handleChamados}>
          <img src={icon} alt="WebChamados" className="icon" />
          <span className="titulo">WebChamados</span>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <NavDropdown title={`Olá, ${name}`} id="basic-nav-dropdown" className="username-dropdown">
              <NavDropdown.Item onClick={handleTrocarSenha}>Trocar a senha</NavDropdown.Item>
              {roleAdmin && <NavDropdown.Item onClick={handleUsuarios} >Usuários</NavDropdown.Item>}
              <NavDropdown.Divider />
              <NavDropdown.Item onClick={handleLogout}>Sair</NavDropdown.Item>
              
            </NavDropdown>
            <NavDropdown title={`Olá, ${name}`} id="mobile-nav-dropdown" className="d-lg-none">
              <NavDropdown.Item onClick={handleTrocarSenha}>Trocar a senha</NavDropdown.Item>
              {roleAdmin && <NavDropdown.Item onClick={handleUsuarios}>Usuários</NavDropdown.Item>}
              <NavDropdown.Divider />
              <NavDropdown.Item onClick={handleLogout}>Sair</NavDropdown.Item>
              
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HeaderNavbar;
