import React , { useState , useEffect } from 'react';
import { useNavigate  } from 'react-router-dom';
import './css/ChamadosIndex.css';
import HeaderNavbar from './HeaderNavbar';
import API_URL from '../utils/apiConfig';
import { AgGridReact } from 'ag-grid-react'; // React Data Grid Component
import "ag-grid-community/styles/ag-grid.css"; // Mandatory CSS required by the grid
import 'ag-grid-community/styles/ag-theme-alpine.css';
import getAxiosInstance from '../utils/axiosInstance';
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import  BtnAbrirChamado  from './btnAbrirChamado';


const ChamadosIndex = () => {
  const [gridApi, setGridApi] = useState(null);
  const [data, setData] = useState([]);
  const navigate = useNavigate(); 
  const [erro, setErro] = useState(null);
  const [msg, setMsg] = useState(null);  
  const [show, setShow] = useState(false);

  const [chamadoSelecionado, setchamadoSelecionado] = useState(null);

  let storedToken = localStorage.getItem('accessToken');
  const baseURLRetornaChamados = `${API_URL}/Chamado/RetornaChamados`
  const baseURLExcluirChamado = `${API_URL}/Chamado/ExcluirChamado`


  const axiosRetornaChamadosInstance = getAxiosInstance(baseURLRetornaChamados, storedToken);
   

  const retornaChamados = async() =>{   

    setErro(null);

    await axiosRetornaChamadosInstance.get(baseURLRetornaChamados)      
    .then(response => {    
      setData(response.data);

     })
    .catch(error=> {
        console.log("Usuarios.refreshResponse.catch - " + error);
        setErro("Erro ao retornar chamados.");   

    })        
  };

  useEffect(() => {

      retornaChamados();  

  }, [] );  

  const onGridReady = (params) => {
    setGridApi(params.api);
  };


  const handleEditarChamado = (chamado) => {
    setchamadoSelecionado(chamado)

    navigate('/EditarChamado', { state: { chamado }});
  };

  const excluirChamado= async() => {

  }


  const columnDefs = [
    { headerName: 'Id', field: 'id' , width: 10},
    { headerName: 'Dt Abertura', field: 'dtAbertura' },
    { headerName: 'Título', field: 'titulo' },
    { headerName: 'Status', field: 'status' },    
    { headerName: 'Solicitante', field: 'solicitante' },
    { headerName: 'Responsavel', field: 'responsavel' },
    { headerName: 'Tipo Conclusão', field: 'conclusao' },
    { headerName: 'Dt Fechamento', field: 'fechamento' },
    {
      headerName: '',
      field: 'abrir',
      width: 100,
      flex: 1,
      cellRenderer: BtnAbrirChamado,
      cellRendererParams: { handleEditarChamado },
      filter: false         
    }
  ];


  const defaultColDef = { 
    sortable: true,
    flex: 1, filter: true,
    floatingFilter: true
  }


  const handleNovoChamado = () => {
    navigate('/novoChamado');
  };

  
  return (
    <div>
      <HeaderNavbar />
      <main className='appp'>
        <div className="container">  

          <h1>Chamados</h1>
            {erro && <div className="alert alert-danger">{erro}</div>}
            {msg && <div className="alert alert-success">{msg}</div>}
            <div align='right'>
              <button type="button" className="btn btn-primary btnIncluir" onClick={handleNovoChamado}>Abrir Chamado</button>
            </div>

            <div className="ag-theme-alpine" style={{ height: '568px', width: '100%' }}>
              <AgGridReact
                rowData={data}
                columnDefs={columnDefs}
                onGridReady={onGridReady}
                defaultColDef={defaultColDef}
                pagination={true}
                paginationPageSize={10}
                frameworkComponents={{ btnAbrirChamado: BtnAbrirChamado }}
                cellRenderer="btnAbrirChamado" 
              />
            </div>
        </div>        
      </main>
    </div>
  );
};

export default ChamadosIndex;
