import './App.css';
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import ChamadosIndex from './components/ChamadosIndex';
import PrivateRoute from './components/PrivateRoute'; // Importe o PrivateRoute
import { jwtDecode } from 'jwt-decode'; 


const App = () => {

  const [roleAdmin, setRoleAdmin] = useState('');

  const token = localStorage.getItem('accessToken');
  if (token) {
    const decodedToken = jwtDecode(token);
    const role = decodedToken['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'];
    setRoleAdmin(role === 'Admin' ? true : false);
  }

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={token ? <Navigate to="/chamados" /> : <LoginForm />}
        />

        <Route element={<PrivateRoute />}>
          <Route path="/chamados" element={<ChamadosIndex />} />
        </Route>


        <Route
          path="/usuarios"
          element={token && roleAdmin ? <Navigate to="/usuarios" /> : "/chamados"}
        />

        <Route element={<PrivateRoute />}>
          <Route path="/usuarios" element={<Usuarios />} />
        </Route>

        <Route
          path="/"
          element={<Navigate to={token ? "/chamados" : "/login"} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
