import React , { useState , useEffect } from 'react';
import './css/Usuarios.css';
import HeaderNavbar from './HeaderNavbar';
import {isAdmin} from '../utils/UserToken';
import { useNavigate  } from 'react-router-dom';
import { AgGridReact } from 'ag-grid-react'; // React Data Grid Component
import "ag-grid-community/styles/ag-grid.css"; // Mandatory CSS required by the grid
import 'ag-grid-community/styles/ag-theme-alpine.css';
import  BtnEditDel  from './BtnEditDel';
import API_URL from '../utils/apiConfig';
import {verificaAutenticacao} from '../utils/UserToken';
import getAxiosInstance from '../utils/axiosInstance';
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";


const Usuarios = () => {
  const [gridApi, setGridApi] = useState(null);
  const [data, setData] = useState([]);
  const navigate = useNavigate(); 
  const [erro, setErro] = useState(null);
  const [msg, setMsg] = useState(null);  
  const [show, setShow] = useState(false);
  const form = useRef(null);

  let storedToken = localStorage.getItem('accessToken');
  const baseURLRetUser = `${API_URL}/Usuario/RetornarUsuarios`
  const baseURLDelUser = `${API_URL}/Usuario/ExcluirUsuario`

  const axiosRetornarUsuariosInstance = getAxiosInstance(baseURLRetUser, storedToken);
   

  const retornaUsuarios = async() =>{   
    await axiosRetornarUsuariosInstance.get(baseURLRetUser)      
    .then(response => {    
      setData(response.data);
      console.log("retornou usuarios");

     })
    .catch(error=> {
        console.log("Usuarios.refreshResponse.catch - " + error);
    })        
  };

  

  useEffect(() => {
    if(isAdmin()){
        retornaUsuarios();
    }
    else{
        navigate('/chamados');
    }
    
  }, [] );  

  function getPerfilLabel(valor) {
    switch (valor) {
      case 1:
        return "Admin";
      case 2:
        return "Dev";
      case 3:
        return "Padrão";
      default:
        return "Desconhecido";
    }
  }

  const onGridReady = (params) => {
    setGridApi(params.api);
  };

  const editarUsuario = (params) => {
    // Implemente a lógica para editar o usuário
    console.log('Usuarios.Editar usuário:', params);
  };

  const excluirUsuario = async(params) => {
    // Implemente a lógica para excluir o usuário

    if(verificaAutenticacao()){

      let storedToken = localStorage.getItem('accessToken');
      
      const axiosExcluirUsuarioInstance = getAxiosInstance(baseURLDelUser, storedToken);

      try {

        console.log(params.email);

        const response = await axiosExcluirUsuarioInstance.delete(`${baseURLDelUser}?email=${params.email}`);

        setErro(null);
        if (response.status === 200) {
          console.log("Usuário excluído com sucesso!");
          setMsg("Usuário excluído com sucesso!");
          retornaUsuarios()
         
        } else {
          console.log("Falha ao excluír usuário.");
          setErro("Erro ao excluído usuário.");
          
        }
      } catch (error) {
        console.log("Erro ao excluír usuário: ", error);
        setErro("Erro ao excluído usuário:" + error);
     
      }    


    }
    else{
      navigate('/login');
    }

    

    
  };
  
  const confirmarExclusaoUsuario = () => {
    
    setShow(true);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    setShow(true);
  };



  const handleClose = () => {
    setShow(false);
  }

  const columnDefs = [
    { headerName: 'Nome', field: 'nome' },
    { headerName: 'Sobrenome', field: 'sobrenome' },
    { headerName: 'Perfil', field: 'perfil', valueGetter: (params) => getPerfilLabel(params.data.perfil) },
    { headerName: 'Email', field: 'email' },
    {
      headerName: 'Ação',
      field: 'actions',
      width: 120,
      flex: 1,
      cellRenderer: BtnEditDel,
      cellRendererParams: { editarUsuario, excluirUsuario: confirmarExclusaoUsuario },
      filter: false         
    }
  ];


  const defaultColDef = {
    sortable: true,
    flex: 1, filter: true,
    floatingFilter: true
  }


  const handleNovoUsuario = () => {
    navigate('/novoUsuario');
  };



  return (
    <div>
      <HeaderNavbar />
      <main className='appp'>
        <div className="container">  

            <h1 className='tituloUsuario'>Usuários</h1>
            {erro && <div className="alert alert-danger">{erro}</div>}
            {msg && <div className="alert alert-success">{msg}</div>}
            <div align='right'>
              <button type="button" className="btn btn-primary btnIncluir" onClick={handleNovoUsuario}>Incluir novo usuário</button>
            </div>

            <div className="ag-theme-alpine" style={{ height: '568px', width: '100%' }}>
              <AgGridReact
                rowData={data}
                columnDefs={columnDefs}
                onGridReady={onGridReady}
                defaultColDef={defaultColDef}
                pagination={true}
                paginationPageSize={10}
                frameworkComponents={{ btnEditDel: BtnEditDel }}
                cellRenderer="btnEditDel" 
              />
            </div>
        </div>
        <form ref={form} onSubmit={handleFormSubmit}>
        
        </form>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Do you really want to submit?</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Cancel
            </Button>
            <Button variant="primary" onClick={excluirUsuario}>
              Confirm
            </Button>
          </Modal.Footer>
        </Modal>
      </main>
    </div>
  );
};

export default Usuarios;
