import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';


export default ({ editarUsuario, excluirUsuario, data }) => {

  const handleEditar = () => {
    editarUsuario(data);
  };

  const handleExcluir = () => {
    excluirUsuario(data);
  };

  return (
    <div className='btn-box-edit-del'>
      <button type="button" className="btn btn-outline-primary btn-square" onClick={handleEditar}> 
          <FontAwesomeIcon icon={faEdit} style={{ fontSize: '14px' }}/>
      </button>
      <button type="button" className="btn btn-outline-primary btn-square"onClick={handleExcluir} > 
          <FontAwesomeIcon icon={faTrash} style={{ fontSize: '14px' }}/>
      </button>
    </div>
  )
}



