import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';


export default ({ editarUsuario, excluirUsuario, data }) => {

  const handleEditar = () => {

    if (data) { 
      editarUsuario(data);
    } else {
      console.log('Dados do usuário não encontrados para edição');
    }
  };

  const handleExcluir = () => {

    if (data) { 
      excluirUsuario(data);
    } else {
      console.log('Dados do usuário não encontrados para exclusão');
    }
  };

  return (
    <div className='btn-box-edit-del'>
      <button type="button" className="btn btn-outline-primary btn-square" onClick={handleEditar}> 
          <FontAwesomeIcon icon={faEdit} style={{ fontSize: '14px' }}/>
      </button>
      <button type="button" className="btn btn-outline-primary btn-square" data-bs-toggle="modal" data-bs-target="#modalExcluir" > 
          <FontAwesomeIcon icon={faTrash} style={{ fontSize: '14px' }}/>
      </button>

    </div>
  )
}
