import React, { createContext, useState, useContext, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';


export const UserContext = createContext(); 

export const UserProvider = ({ children }) => {
    
  const [user, setUser] = useState(null);
  const [tokenExpired, setTokenExpired] = useState(false);


  const updateTokenExpired = (expired) => {
    setTokenExpired(expired);
  };

  const updateUser = () => {
    
    let decodedToken = getTokenFromStorage();
    
    setUser({
      name: token.name,
      email: token.email,
      role: token.role,
      exp: token.exp
      });
  };

  // Função para buscar o token do armazenamento local
  const getTokenFromStorage = () => {
    const storedToken = localStorage.getItem('accessToken');

    if (storedToken) {
      const decodedToken = jwtDecode(storedToken);
      return {
        name: decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'],
        email: decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/emailaddress'],
        role: decodedToken['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'],
        exp: decodedToken.exp
      }
    }

    return null;
    
  };

  // busca o refresh token do storage
  const getRefreshTokenFromStorage = () => {
    const storedToken = localStorage.getItem('refreshToken');

    console.log("getRefreshTokenFromStorage 1");


    if (storedToken) {    
      console.log("getRefreshTokenFromStorage 2");  
      return storedToken
    }

    console.log("getRefreshTokenFromStorage 3");
    return null;
    
  };



  useEffect(() => {

    const checkLogin = async () => 
    {

      try 
      {
    
        let token = getTokenFromStorage();

        console.log("a");

        if (token) 
        {
        
          console.log("b");

          const currentTime = Date.now() / 1000;

          if (token.exp < currentTime) 
          {          

              console.log("c");
              console.log("token.exp: " + token.exp);       
              
              const refreshToken = getRefreshTokenFromStorage() ;

              console.log("token.exp: " + refreshToken);  
          
              //Se token expirado , verifica o refresh
              const refreshResponse = await axios.post('https://localhost:7137/api/Usuario/Refresh',  refreshToken );

              console.log("c.1");
              console.log(refreshResponse.status)
              console.log(refreshResponse.data.flag)
          
              if (refreshResponse.data.flag) {

                console.log("d");

                  const { accessToken: newAccessToken, refreshToken: newRefreshToken } = refreshResponse.data;
                  localStorage.setItem('accessToken', newAccessToken);
                  localStorage.setItem('refreshToken', newRefreshToken);
                  
                  token = jwtDecode(newAccessToken);

                  setTokenExpired(false)
                  
                  setUser({
                      name: token['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'],
                      email: token['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/emailaddress'],
                      role: token['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'],
                      exp: token.exp
                    });
              }
              else
              {
                
                console.log("e");

                // refresh token inválido
                  setUser(null);
                  setTokenExpired(true);
                
              }
            
          } 
          else 
          {
          console.log("f");
          
          
          // token válido , retorna as informações

          console.log(token.name);
          console.log(token.email);
          console.log(token.role);
          console.log(token.exp);
          
          setTokenExpired(false)

          setUser({
            name: token.name,
            email: token.email,
            role: token.role,
            exp: token.exp
            });
          }
        } 
        else 
        {
          console.log("g");
          // não foi encontrado token no storage

          setUser(null);
          setTokenExpired(true) ;
        
        
          console.log(tokenExpired);
        
       

        }

      } catch (error) 
      {

        console.log("catch : " )

        console.error("Refresh token failed", error);
        setUser(null);
        setTokenExpired(true);
      }
      

    };

    
    checkLogin();
    

  }, [user , tokenExpired]);

  return (
    <UserContext.Provider value={{ user, tokenExpired , updateTokenExpired , updateUser}}>
      {children}
    </UserContext.Provider>
  );
};

export const useUser = () => useContext(UserContext);
