import React, { useState, useEffect,useRef  } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Alteração feita aqui
import './ChamadosIndex.css';
import icon from '../img/icone.png';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';

const ChamadosIndex = () => {
  
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [roleAdmin, setRoleAdmin] = useState('');
  

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (token) {
      const decodedToken = jwtDecode(token);
      const name = decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'];
      setUsername(name); // Atualizando o nome do usuário
      
      const role = decodedToken['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'];
      setRoleAdmin(role === 'Admin' ? true : false);

    } else {
      navigate('/'); // Redireciona para a página de login se não houver token
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    navigate('/');
  };

  const handleChangePassword = () => {
    // Lógica para mudar a senha
    console.log('Trocar a senha');
  };

  

  return (
    <div>
      <Navbar bg="light" expand="lg" className="custom-navbar">
        <Container>
          <Navbar.Brand href="#home" className="d-flex align-items-center">
            <img src={icon} alt="WebChamados" className="icon" />
            <span className="titulo">WebChamados</span>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto">
              <NavDropdown title={`Olá, ${username}`} id="basic-nav-dropdown" className="username-dropdown">
                <NavDropdown.Item onClick={handleChangePassword}>Trocar a senha</NavDropdown.Item>
                {roleAdmin && <NavDropdown.Item href="/usuarios">Usuários</NavDropdown.Item>}
                <NavDropdown.Divider />
                <NavDropdown.Item onClick={handleLogout}>Sair</NavDropdown.Item>                             
              </NavDropdown>
              <NavDropdown title={`Olá, ${username}`} id="mobile-nav-dropdown" className="d-lg-none">
                <NavDropdown.Item onClick={handleChangePassword}>Trocar a senha</NavDropdown.Item>
                {roleAdmin && <NavDropdown.Item href="/usuarios">Usuários</NavDropdown.Item>}
                <NavDropdown.Divider />
                <NavDropdown.Item onClick={handleLogout}>Sair</NavDropdown.Item>                
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <main>
        <div className="container">
          <h1>Lista de Chamados</h1>
        </div>
      </main>
    </div>
  );
};

export default ChamadosIndex;
