import './App.css';
import React , {useState , useEffect, useContext} from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import ChamadosIndex from './components/ChamadosIndex';
import Usuarios from './components/Usuarios';
import TrocarSenha from './components/TrocarSenha';
import PrivateRoute from './components/PrivateRoute'; // Importe o PrivateRoute

import { UserProvider, UserContext } from './components/UserContext';

const App = () => {

  const [roleAdmin, setRoleAdmin] = useState('');

  const { tokenExpired , user} = useContext(UserContext); 

  useEffect(() => {
    if(!tokenExpired){

        setRoleAdmin(user.role === 'Admin' ? true : false);

    }

  }, []);



  return (
    <Router>
      <UserProvider>  
        <Routes>
          <Route path="/login" element={token ? <Navigate to="/chamados" /> : <LoginForm />} />

          <Route path="/trocarSenha" element={token ? <TrocarSenha /> : <LoginForm />} />

          <Route element={<PrivateRoute />}>
            <Route path="/chamados" element={<ChamadosIndex />} />          
          </Route>

          <Route path="/usuarios" element={roleAdmin ? <Usuarios /> : <Navigate to="/chamados" />} />


          <Route
            path="/"
            element={<Navigate to={token ? "/chamados" : "/login"} />}
          />
        </Routes>
      </UserProvider>
    </Router>
  );
};

export default App;
