import './App.css';
import React , {useState , useEffect, useContext} from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import ChamadosIndex from './components/ChamadosIndex';
import Usuarios from './components/Usuarios';
import TrocarSenha from './components/TrocarSenha';
import PrivateRoute from './components/PrivateRoute'; 
import { UserContext } from './components/UserContext';

const App = () => {

  const [roleAdmin, setRoleAdmin] = useState(false);

  const { user, tokenExpired , checkLogin} = useContext(UserContext); 
  //let roleAdmin  = false;

  useEffect(() => {

    console.log("0");

    checkLogin();

    console.log(tokenExpired);


    if(tokenExpired  || !user){
      console.log("1");
      roleAdmin  = false;
      console.log("1.1");
    }
    else
    {    
      console.log("2");
      roleAdmin  =  (user.role === 'Admin' ?  true : false );
      console.log("2.2");
    }

  }, []);  


  return (
    <Router>

        <Routes>
          <Route path="/login" element={!tokenExpired ? <Navigate to="/chamados" /> : <LoginForm />} />

          <Route path="/trocarSenha" element={!tokenExpired ? <TrocarSenha /> : <LoginForm />} />

          <Route element={<PrivateRoute />}>
            <Route path="/chamados" element={<ChamadosIndex />} />          
          </Route>

          <Route path="/usuarios" element={roleAdmin ? <Usuarios /> : <Navigate to="/chamados" />} />


          <Route
            path="/"
            element={<Navigate to={!tokenExpired ? "/chamados" : "/login"} />}
          />
        </Routes>

    </Router>
  );
};

export default App;
