import React , { useState , useEffect } from 'react';
import './css/Usuarios.css';
import HeaderNavbar from './HeaderNavbar';
import axios from 'axios';
import isAdmin from '../utils/UserToken';
import { useNavigate  } from 'react-router-dom';

const Usuarios = () => {
  
  const [data, setData] = useState([]);
  const navigate = useNavigate(); 

  const storedToken = localStorage.getItem('accessToken');

  const baseURL = 'https://localhost:7137/api/Usuario/RetornarUsuarios';

  const axiosInstance = axios.create({
    baseURL,
    headers:{Authorization: `Bearer ${storedToken}`}
  });

  axiosInstance.interceptors.request.use(async (request) => {                                      
    request.headers.Authorization = `Bearer ${storedToken}`;                   
    return request;
  });
      


  const retornaUsuarios = async() =>{   
    await axiosInstance.get(baseURL)      
    .then(response => {    
      setData(response.data);
      console.log("chegou aqui no then");

     })
    .catch(error=> {
        console.log("Usuarios.refreshResponse.catch - " + error);
    })        
  };


  useEffect(() => {
    if(isAdmin()){
        retornaUsuarios();
    }
    else{
        navigate('/chamados');
    }
    
  }, [] );  

  
  return (
    <div>
      <HeaderNavbar />
      <main>
        <div className="container">          
          <h1>Lista de usuarios</h1>

          <header>

          </header>
          <table className="">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Sobrenome</th>
                <th>Email</th>
                <th>Perfil</th>
              </tr>
            </thead>
            <tbody>
              {data.map(usuario=>(
                <tr key={usuario.email}>
                  <td>{usuario.nome}</td>
                  <td>{usuario.sobrenome}</td>
                  <td>{usuario.email}</td>
                  <td>{usuario.perfil}</td>
                </tr>
              ))}
            </tbody>

          </table>

          <form onSubmit={retornaUsuarios}>
            <button type="submit">carregar usuarios</button>
          </form>
         
        </div>
      </main>
    </div>
  );
};

export default Usuarios;
