import React, { useState } from 'react';
import axios from 'axios';
import './LoginForm.css';
import { useHistory } from 'react-router-dom';

const LoginForm = () => {
    
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://localhost:7137/api/Usuario/Login', {
        email,
        password,
      });

      
      if (response.data.flag) {
        
        localStorage.setItem('accessToken', response.data.accessToken);
        localStorage.setItem('refreshToken', response.data.refreshToken);
        history.push('/logado');

      } else {
        // Se não, exiba a mensagem de erro
        setError(response.data.message);
      }
    } catch (error) {
        console.error('Erro de login:', error);
        setError('Erro ao efetuar o login. Por favor, tente novamente.');
    }
  };

  
  return (
    <div className="formContainer">
        <div className="formBox">
            <h1  className="appName" >WebChamados</h1>
            <h2 className="telaLogin">Fazer login</h2>
            {error && <p className="error">{error}</p>} 
            <form onSubmit={handleSubmit}>
                <input type="email" placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)}/>
                <input type="password" placeholder='Senha'  value={password} onChange={(e) => setPassword(e.target.value)}/>
                <button type="submit">Fazer login</button>
            </form>
            <div className="forgotPassword">
                <div className="divider"></div>
                <a href="#">Esqueceu sua senha?</a>                
            </div>            
        </div>
    </div>
  );

};

export default LoginForm;
