import axios from 'axios'
import { jwtDecode } from 'jwt-decode';


const baseURL = 'https://localhost:7137'

let storedAccessToken = localStorage.getItem('accessToken') ;
let storedRefreshToken = localStorage.getItem('refreshToken');


const axiosInstance = axios.create({
    baseURL,
    headers:{Authorization: `Bearer ${storedAccessToken}`}
});

axiosInstance.interceptors.request.use(async req => {
    if(!storedAccessToken){
        storedAccessToken = localStorage.getItem('accessToken') ;
        req.headers.Authorization = `Bearer ${storedAccessToken}`
    }

    const token = jwtDecode(storedAccessToken);

    const currentTime = Date.now() / 1000;

    const isExpired = token.exp < currentTime;

    if(!isExpired) return req;

    if(!storedRefreshToken){
        storedRefreshToken = localStorage.getItem('refreshToken') ;
        req.headers.Authorization = `Bearer ${storedRefreshToken}`
    }

    const refreshResponse = await axios.post(`${baseURL}/api/Usuario/Refresh/`);

    if (refreshResponse.data.flag) {
        const { accessToken: newAccessToken, refreshToken: newRefreshToken } = refreshResponse.data;
        localStorage.setItem('accessToken', newAccessToken);
        localStorage.setItem('refreshToken', newRefreshToken);
        
        req.headers.Authorization = `Bearer ${newAccessToken}`;

    }
    
    
    
    return req;
})


export default axiosInstance;
