import React, { useState , useEffect , useContext }from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import icon from '../img/icone.png';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; 
import './css/HeaderNavbar.css';
import { UserContext } from './UserContext';


const HeaderNavbar = () => {

  const [username, setUsername] = useState("");
  const [roleAdmin, setRoleAdmin] = useState(false);
  const navigate = useNavigate();
  //const [redirected, setRedirected] = useState(false); // Variável de controle

  let varRoleAdmin = false;
  let varUserName = "";
  
  const { user, tokenExpired , checkLogin} = useContext(UserContext); 

  useEffect(() => {
    
    checkLogin();

    console.log("HeaderNavBar.userEffect - Inicio ");

    if (tokenExpired) {

      //if (!redirected) {

        console.log('Redirecting to login...');

        navigate('/login');
        setRedirected(true); 
      //}
      
      console.log("x");

    }
    else if (!user)
    {
      //if (!redirected) {
        console.log('User not found, redirecting to login...');
        navigate('/login');
        setRedirected(true);
      //}

      console.log("w");

    } else {

      console.log('Setting username and role...');    


      //setRedirected(false);

      varRoleAdmin = (user.role === 'Admin' ? true : false);
      varUserName = user.name;


      setRoleAdmin(varRoleAdmin);
      setUsername(varUserName);

      console.log("HeaderNavBar.userEffect - roleAdmin: " + varRoleAdmin);
      console.log("HeaderNavBar.userEffect - username: " + varUserName);


    }
  }, []);



  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    checkLogin();
    navigate('/login');
  };


  const handleUsuarios = () => {
    console.log("HeaderNavBar.handleUsuarios");
    navigate('/usuarios');
  };

  const handleChamados = () => {
    navigate('/chamados');
  };

  const handleTrocarSenha = () => {
    navigate('/trocarSenha');
  };
  

  return (
    <Navbar bg="light" expand="lg" className="custom-navbar">
      <Container>
        <Navbar.Brand href="#home" className="d-flex align-items-center" onClick={handleChamados}>
          <img src={icon} alt="WebChamados" className="icon" />
          <span className="titulo">WebChamados</span>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <NavDropdown title={`Olá, ${username}`} id="basic-nav-dropdown" className="username-dropdown">
              <NavDropdown.Item onClick={handleTrocarSenha}>Trocar a senha</NavDropdown.Item>
              {roleAdmin && <NavDropdown.Item onClick={handleUsuarios} >Usuários</NavDropdown.Item>}
              <NavDropdown.Divider />
              <NavDropdown.Item onClick={handleLogout}>Sair</NavDropdown.Item>
              
            </NavDropdown>
            <NavDropdown title={`Olá, ${username}`} id="mobile-nav-dropdown" className="d-lg-none">
              <NavDropdown.Item onClick={handleTrocarSenha}>Trocar a senha</NavDropdown.Item>
              {roleAdmin && <NavDropdown.Item onClick={handleUsuarios}>Usuários</NavDropdown.Item>}
              <NavDropdown.Divider />
              <NavDropdown.Item onClick={handleLogout}>Sair</NavDropdown.Item>
              
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HeaderNavbar;
