import HeaderNavbar from './HeaderNavbar';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API_URL from '../utils/apiConfig';
import getAxiosInstance from '../utils/axiosInstance';


const AbrirChamado = () => {
    const navigate = useNavigate();
    const [titulo, setTitulo] = useState('');
    const [descricao, setDescricao] = useState('');
    const [erro, setErro] = useState(null);
    const [msg, setMsg] = useState(null);


    const handleSalvar = () => {
        
        setErro(null);
        setMsg(null);

        if(validaCampos()) {
            IncluiNovoUsuario();
        }            

    };

    const handleCancelar = () => {
        navigate('/Chamados');
    };

    const handleLimpar = () => {
        setTitulo("");
        setDescricao("");
        setErro(null);
        setMsg(null);
    };
    

    return (
        <div>
          <HeaderNavbar />
          <main className='appp'>
            <div className="container">

                <h1>Abrir Chamado</h1>
                {erro && <div className="alert alert-danger">{erro}</div>}
                {msg && <div className="alert alert-success">{msg}</div>}

                <div className="col-md-12">
                    <label htmlFor="validationtitulo" className="form-label">Título Chamado</label>
                    <input type="text" className="form-control" id="validationtitulo" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
                </div>

                <div className="">
                    <label htmlFor="validationtitulo" className="form-label">Descrição</label>
                    <textarea type="textarea" className="form-control" id="validationtitulo" value={descricao} onChange={(e) => setDescricao(e.target.value)} required />
                </div>

                <div className="mb-3">
                    <button type="button" className="btn btn-primary " onClick={handleSalvar}>Salvar</button>
                    <button type="button" className="btn btn-primary " onClick={handleCancelar}>Cancelar</button>
                    <button type="button" className="btn btn-primary " onClick={handleLimpar}>Limpar Campos</button>
                </div>

            </div>
      </main>
    </div>
  );
};

export default AbrirChamado;
