import React, { useState, useEffect,useRef  } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Alteração feita aqui
import './ChamadosIndex.css';
import icon from '../img/icone.png';

const ChamadosIndex = () => {
  
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [menuVisible, setMenuVisible] = useState(false);
  const menuRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (token) {
      const decodedToken = jwtDecode(token);
      const name = decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'];
      setUsername(name); // Atualizando o nome do usuário
    } else {
      navigate('/login'); // Redireciona para a página de login se não houver token
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    navigate('/login');
  };

  const handleChangePassword = () => {
    // Lógica para mudar a senha
    console.log('Trocar a senha');
  };

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  const handleClickOutside = (event) => {
    if (menuRef.current && !menuRef.current.contains(event.target)) {
      setMenuVisible(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div>
      <header className="header">
        <div className="header-container">
          <div className="header-left">
            <img src={icon} alt="WebChamados" className="icon" />
            <span><h2>WebChamados</h2></span>
          </div>
          <div className="header-right">
            <div className="user-menu"  onClick={toggleMenu} ref={menuRef}>
              <span className="saudacao">Olá, {username}</span>
              {menuVisible && (
                <div className="user-menu-content" >
                  <a href="#" onClick={handleChangePassword}>Trocar a senha</a>
                  <a href="#" onClick={handleLogout}>Sair</a>
                </div>
              )}
            </div>
          </div>
        </div>
      </header>
      <main>
        <div className="container">
          <h1>Lista de Chamados</h1>
          {/* Lista de chamados será implementada aqui */}
        </div>
      </main>
    </div>
  );
};

export default ChamadosIndex;
