import React , { useState , useEffect } from 'react';
import './css/Usuarios.css';
import HeaderNavbar from './HeaderNavbar';
import axios from 'axios';
import {isAdmin} from '../utils/UserToken';
import { useNavigate  } from 'react-router-dom';

const Usuarios = () => {
  
  const [data, setData] = useState([]);
  const navigate = useNavigate(); 

  const storedToken = localStorage.getItem('accessToken');

  const baseURL = 'https://localhost:7137/api/Usuario/RetornarUsuarios';

  const axiosInstance = axios.create({
    baseURL,
    headers:{Authorization: `Bearer ${storedToken}`}
  });

  axiosInstance.interceptors.request.use(async (request) => {                                      
    request.headers.Authorization = `Bearer ${storedToken}`;                   
    return request;
  });
      


  const retornaUsuarios = async() =>{   
    await axiosInstance.get(baseURL)      
    .then(response => {    
      setData(response.data);
      console.log("chegou aqui no then");

     })
    .catch(error=> {
        console.log("Usuarios.refreshResponse.catch - " + error);
    })        
  };


  useEffect(() => {
    if(isAdmin()){
        retornaUsuarios();
    }
    else{
        navigate('/chamados');
    }
    
  }, [] );  

  
  return (
    <div>
      <HeaderNavbar />
      <main>
        <div className="container">          
          <h1 className='aaaaaaaaaaa'>Usuários</h1>

          <header>
            <button type="button" className="btn btn-primary btnIncluir">Incluir novo usuário</button>
          </header>
          <table className="table">
            <thead>
              <tr>
                <th scope = "col">Nome</th>
                <th scope = "col">Sobrenome</th>
                <th scope = "col">Email</th>
                <th scope = "col">Perfil</th>
                <th scope = "col"></th>
              </tr>
            </thead>
            <tbody>
              {data.map(usuario=>(
                <tr key={usuario.email}>
                  <td scope="row">{usuario.nome}</td>
                  <td>{usuario.sobrenome}</td>
                  <td>{usuario.email}</td>
                  <td>{usuario.perfil}</td>
                  <td>
                    <button type="button" className="btn btn-outline-primary">Editar</button>
                    <button type="button" className="btn btn-outline-primary">Excluir</button>
                  </td>
                </tr>
              ))}
            </tbody>

          </table>

        </div>
      </main>
    </div>
  );
};

export default Usuarios;
