import React, { createContext, useState, useContext, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
//import { useNavigate } from 'react-router-dom';

const UserContext = createContext();

export const UserProvider = ({ children }) => {
    
  const [user, setUser] = useState(null);
  const [tokenExpired, setTokenExpired] = useState(false);

  //const navigate = useNavigate();


  useEffect(async () => {
    const token = localStorage.getItem('accessToken');
    const refreshToken = localStorage.getItem('refreshToken');

    if (token) {
      const decodedToken = jwtDecode(token);
      const currentTime = Date.now() / 1000;

      if (decodedToken.exp < currentTime) {

        //token expirado , verifica o refresh
        const refreshResponse = await axios.post('https://localhost:7137/api/Usuario/Refresh', { refreshToken, });
        
        if (refreshResponse.data.flag) {
            const { accessToken: newAccessToken, refreshToken: newRefreshToken } = refreshResponse.data;
            localStorage.setItem('accessToken', newAccessToken);
            localStorage.setItem('refreshToken', newRefreshToken);
            
            decodedToken = jwtDecode(newAccessToken);
            
            setUser({
                name: decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'],
                email: decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/emailaddress'],
                role: decodedToken['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'],
                exp: decodedToken.exp
              });
        }
        else
        {
            setUser(null);
            setTokenExpired(true);
            //navigate('/login');
        }
        
      } 
      else 
      {
        setUser({
          name: decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'],
          email: decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/emailaddress'],
          role: decodedToken['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'],
          exp: decodedToken.exp
        });
      }
    } else {
      setUser(null);
      setTokenExpired(true);
      //navigate('/login');
    }
  }, []);

  return (
    <UserContext.Provider value={{ user, tokenExpired }}>
      {children}
    </UserContext.Provider>
  );
};

export const useUser = () => useContext(UserContext);
