import React , { useState , useEffect } from 'react';
import './css/Usuarios.css';
import HeaderNavbar from './HeaderNavbar';
import axios from 'axios';
import {isAdmin} from '../utils/UserToken';
import { useNavigate  } from 'react-router-dom';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { AgGridReact } from 'ag-grid-react'; // React Data Grid Component
import "ag-grid-community/styles/ag-grid.css"; // Mandatory CSS required by the grid
import 'ag-grid-community/styles/ag-theme-alpine.css';
import  BtnEditDel  from './BtnEditDel';
import API_URL from '../utils/apiConfig';

const Usuarios = () => {
  const [gridApi, setGridApi] = useState(null);
  const [columnApi, setColumnApi] = useState(null);
  const [data, setData] = useState([]);
  const navigate = useNavigate(); 
  

  const storedToken = localStorage.getItem('accessToken');

  const baseURL = `${API_URL}/Usuario/RetornarUsuarios`

  const axiosInstance = axios.create({
    baseURL,
    headers:{Authorization: `Bearer ${storedToken}`}
  });

  axiosInstance.interceptors.request.use(async (request) => {                                      
    request.headers.Authorization = `Bearer ${storedToken}`;                   
    return request;
  });     


  const retornaUsuarios = async() =>{   
    await axiosInstance.get(baseURL)      
    .then(response => {    
      setData(response.data);
      console.log("retornou usuarios");

     })
    .catch(error=> {
        console.log("Usuarios.refreshResponse.catch - " + error);
    })        
  };

  

  useEffect(() => {
    if(isAdmin()){
        retornaUsuarios();
    }
    else{
        navigate('/chamados');
    }
    
  }, [] );  

  function getPerfilLabel(valor) {
    switch (valor) {
      case 1:
        return "Admin";
      case 2:
        return "Dev";
      case 3:
        return "Padrão";
      default:
        return "Desconhecido";
    }
  }

  const onGridReady = (params) => {
    setGridApi(params.api);
  };

  const editarUsuario = (params) => {
    // Implemente a lógica para editar o usuário
    console.log('Usuarios.Editar usuário:', params);
  };

  const excluirUsuario = (params) => {
    // Implemente a lógica para excluir o usuário
    console.log('Excluir usuário:', params);
  };
  

  const columnDefs = [
    { headerName: 'Nome', field: 'nome' },
    { headerName: 'Sobrenome', field: 'sobrenome' },
    { headerName: 'Perfil', field: 'perfil', valueGetter: (params) => getPerfilLabel(params.data.perfil) },
    { headerName: 'Email', field: 'email' },
    {
      headerName: 'Ação',
      field: 'actions',
      width: 120,
      flex: 1,
      cellRenderer: BtnEditDel,
      cellRendererParams: { editarUsuario, excluirUsuario },
      filter: false         
    }
  ];


  const defaultColDef = {
    sortable: true,
    flex: 1, filter: true,
    floatingFilter: true
  }


  const handleNovoUsuario = () => {
    navigate('/novoUsuario');
  };



  return (
    <div>
      <HeaderNavbar />
      <main className='appp'>
        <div className="container">  

            <h1 className='tituloUsuario'>Usuários</h1>

            <div align='right'>
              <button type="button" className="btn btn-primary btnIncluir" onClick={handleNovoUsuario}>Incluir novo usuário</button>
            </div>

            <div className="ag-theme-alpine" style={{ height: '568px', width: '100%' }}>
              <AgGridReact
                rowData={data}
                columnDefs={columnDefs}
                onGridReady={onGridReady}
                defaultColDef={defaultColDef}
                pagination={true}
                paginationPageSize={10}
                frameworkComponents={{ btnEditDel: BtnEditDel }} // Adicione essa linha
                cellRenderer="btnEditDel" // Altere esta linha
              />
            </div>

        </div>
      </main>
    </div>
  );
};

export default Usuarios;
