import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';
import './ChamadosIndex.css';
import icon from '../img/icone.png';

const ChamadosIndex = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (token) {
      const decodedToken = jwtDecode(token);
      const name = decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'];
      setUsername(name); // Atualizando o nome do usuário
    } else {
      navigate('/login'); // Redireciona para a página de login se não houver token
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    navigate('/login');
  };

  const handleChangePassword = () => {
    // Lógica para mudar a senha
    console.log('Trocar a senha');
  };

  return (
    <div>
      <header className="header">
        <div className="header-left">
          <img src={icon} alt="WebChamados" className="icon" />
          <span>WebChamados</span>
        </div>
        <div className="header-right">
          <div className="user-menu">
            <span>Olá, {username}</span>
            <div className="user-menu-content">
              <a href="#" onClick={handleChangePassword}>Trocar a senha</a>
              <a href="#" onClick={handleLogout}>Sair</a>
            </div>
          </div>
        </div>
      </header>
      <main>
        <h1>Lista de Chamados</h1>
        {/* Lista de chamados será implementada aqui */}
      </main>
    </div>
  );
};

export default ChamadosIndex;
