import './App.css';
import React , {useState , useEffect, useContext} from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import ChamadosIndex from './components/ChamadosIndex';
import Usuarios from './components/Usuarios';
import TrocarSenha from './components/TrocarSenha';
import PrivateRoute from './components/PrivateRoute'; 
import { UserContext } from './components/UserContext';

const App = () => {

  const [roleAdmin, setRoleAdmin] = useState(false);

  const { user, tokenExpired , checkLogin} = useContext(UserContext); 

  let varRoleAdmin = false;

  useEffect(() => {

    console.log("App.useEffect");

    //checkLogin();

    console.log("App.useEffect - tokenEspired: " + tokenExpired);


    if(tokenExpired  || !user){
      console.log("App.useEffect - 1");
      setRoleAdmin(false);
      console.log("App.useEffect - tokenExpired: " + tokenExpired);
      console.log("App.useEffect - roleAdmin: " + roleAdmin);
    }
    else
    {    

      console.log("App.useEffect - 2");
      console.log("App.useEffect - user.role: " + user.role);


      varRoleAdmin = (user.role === 'Admin' ? true : false);
      console.log("App.useEffect - varRoleAdmin: " + varRoleAdmin);

      console.log("App.useEffect - antes roleAdmin: " + roleAdmin);
      setRoleAdmin(varRoleAdmin);
      console.log("App.useEffect - depois roleAdmin: " + roleAdmin);


      console.log("App.useEffect - tokenExpired: " + tokenExpired);

    }

  }, [ tokenExpired]);  


  return (
    <Router>

        <Routes>
          <Route path="/" 
                 element={<Navigate to={!tokenExpired ? "/chamados" : "/login"} />}
          />

          <Route path="/login" 
                 element={!tokenExpired ? <Navigate to="/chamados" /> : <LoginForm />} />

          <Route path="/trocarSenha" 
                element={!tokenExpired ? <TrocarSenha /> : <LoginForm />} />



          {/*<Route path="/usuarios" element={roleAdmin ? <Usuarios /> : <Navigate to="/chamados" />} />*/}
          {/*<Route path="/usuarios" element={roleAdmin ? <Navigate to="/usuarios" /> : <Navigate to="/chamados" />} />*/}

          <Route path="/usuarios" element={<Usuarios />}/>


          <Route element={<PrivateRoute />}>
            <Route path="/chamados" element={<ChamadosIndex />} />          
          </Route>

          <Route element={<PrivateRoute />}>
            <Route path="/usuarios" element={<Usuarios />} />          
          </Route>


        </Routes>

    </Router>
  );
};

export default App;
