import React, { useState, useEffect,useRef  } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Alteração feita aqui
import './ChamadosIndex.css';
import icon from '../img/icone.png';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';

const ChamadosIndex = () => {
  
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (token) {
      const decodedToken = jwtDecode(token);
      const name = decodedToken['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'];
      setUsername(name); // Atualizando o nome do usuário
    } else {
      navigate('/login'); // Redireciona para a página de login se não houver token
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    navigate('/login');
  };

  const handleChangePassword = () => {
    // Lógica para mudar a senha
    console.log('Trocar a senha');
  };

  

  return (
    <div>
      <Navbar bg="light" expand="lg" className="custom-navbar">
        <Container>
          <Navbar.Brand href="#home">
            <img src={icon} alt="WebChamados" className="icon" />
            WebChamados
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              <NavDropdown title={`Olá, ${username}`} id="basic-nav-dropdown">
                <NavDropdown.Item onClick={handleChangePassword}>Trocar a senha</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item onClick={handleLogout}>Sair</NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <main>
        <div className="container">
          <h1>Lista de Chamados</h1>
        </div>
      </main>
    </div>
  );
};

export default ChamadosIndex;
