import React, { useState } from 'react';
import axios from 'axios';
import './LoginForm.css';

const LoginForm = () => {
    
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://localhost:7137/api/Usuario/Login', {
        email,
        password,
      });
      // Armazene os tokens JWT recebidos no local storage ou em um estado global (ex: Redux)
      console.log('Token de acesso:', response.data.access_token);
      console.log('Token de refresh:', response.data.refresh_token);
    } catch (error) {
      console.error('Erro de login:', error);
    }
  };

  /*
  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button type="submit">Login</button>
    </form>
  );
  */
  return (
    <div className="formContainer">
        <div className="formBox">
            <h1  className="appName" >WebChamados</h1>
            <h2 className="telaLogin">Fazer login</h2>
            <form onSubmit={handleSubmit}>
                <input type="email" placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)}/>
                <input type="password" placeholder='Senha'  value={password} onChange={(e) => setPassword(e.target.value)}/>
                <button type="submit">Fazer login</button>
            </form>
            <div className="forgotPassword">
                <div className="divider"></div>
                <a href="#">Esqueceu sua senha?</a>                
            </div>            
        </div>
    </div>
  );

};

export default LoginForm;
