import React , { useState , useEffect } from 'react';
import './css/Usuarios.css';
import HeaderNavbar from './HeaderNavbar';
import axios from 'axios';
import {isAdmin} from '../utils/UserToken';
import { useNavigate  } from 'react-router-dom';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


const Usuarios = () => {
  
  const [data, setData] = useState([]);
  const navigate = useNavigate(); 

  const storedToken = localStorage.getItem('accessToken');

  const baseURL = 'https://localhost:7137/api/Usuario/RetornarUsuarios';

  const axiosInstance = axios.create({
    baseURL,
    headers:{Authorization: `Bearer ${storedToken}`}
  });

  axiosInstance.interceptors.request.use(async (request) => {                                      
    request.headers.Authorization = `Bearer ${storedToken}`;                   
    return request;
  });
      


  const retornaUsuarios = async() =>{   
    await axiosInstance.get(baseURL)      
    .then(response => {    
      setData(response.data);
      console.log("chegou aqui no then");

     })
    .catch(error=> {
        console.log("Usuarios.refreshResponse.catch - " + error);
    })        
  };


  useEffect(() => {
    if(isAdmin()){
        retornaUsuarios();
    }
    else{
        navigate('/chamados');
    }
    
  }, [] );  

  function getPerfilLabel(valor) {
    switch (valor) {
      case 1:
        return "Admin";
      case 2:
        return "Dev";
      case 3:
        return "PadrÃ£o";
      default:
        return "Desconhecido";
    }
  }
  
  
  return (
    <div>
      <HeaderNavbar />
      <main>
        <div className="container">          
          <h1 className='tituloUsuario'>UsuÃ¡rios</h1>

          <header className='text-right'>
            <button type="button" className="btn btn-primary btnIncluir">Incluir novo usuÃ¡rio</button>
          </header>
          <table className="table">
            <thead>
              <tr>
                <th scope = "col">Nome</th>
                <th scope = "col">Sobrenome</th>
                <th scope = "col">Email</th>
                <th scope = "col">Perfil</th>
                <th scope = "col"></th>
              </tr>
            </thead>
            <tbody>
              {data.map(usuario=>(
                <tr key={usuario.email}>
                  <td scope="row">{usuario.nome}</td>
                  <td>{usuario.sobrenome}</td>
                  <td>{usuario.email}</td>
                  <td>{getPerfilLabel(usuario.perfil)}</td>
                  <td>
                    <button type="button" className="btn btn-outline-primary btn-square"><FontAwesomeIcon icon={faEdit} /></button>
                    <button type="button" className="btn btn-outline-primary btn-square"><FontAwesomeIcon icon={faTrash} /></button>
                  </td>
                </tr>
              ))}
            </tbody>

          </table>

        </div>
      </main>
    </div>
  );
};

export default Usuarios;
